import React, { useState } from 'react';
import './Services.css';

const Services = () => {
  const [activeService, setActiveService] = useState(null);
  
  const services = [
    {
      id: 1,
      title: 'Exterior Detailing',
      icon: 'fa-car',
      shortDesc: 'Washing, waxing, and buffing to restore your car\'s exterior.',
      longDesc: 'Our exterior detailing service includes a thorough hand wash, clay bar treatment to remove contaminants, polishing to eliminate swirl marks and light scratches, and a high-quality wax application for long-lasting protection and shine. We also clean and dress tires and wheel wells for a complete look.'
    },
    {
      id: 2,
      title: 'Interior Detailing',
      icon: 'fa-couch',
      shortDesc: 'Vacuuming, shampooing, and odor removal for a fresh interior.',
      longDesc: 'Our interior detailing service includes thorough vacuuming of all surfaces, steam cleaning of carpets and upholstery, cleaning and conditioning of leather surfaces, dusting and sanitizing of all panels and components, and application of protectants to prevent UV damage. We also deodorize the interior to leave it smelling fresh.'
    },
    {
      id: 3,
      title: 'Full Detail Package',
      icon: 'fa-spray-can',
      shortDesc: 'Complete interior and exterior detailing for a showroom finish.',
      longDesc: 'Our full detail package combines our exterior and interior detailing services for a comprehensive transformation of your vehicle. This service includes everything from paint correction and protection to deep interior cleaning and sanitizing. Perfect for vehicles that need special attention or for those who want the absolute best for their car.'
    },
    {
      id: 4,
      title: 'Maintenance Wash',
      icon: 'fa-tint',
      shortDesc: 'Regular washing to maintain your vehicle\'s appearance between details.',
      longDesc: 'Our maintenance wash service is designed to keep your vehicle looking great between full detailing sessions. It includes a thorough hand wash, quick interior vacuum and wipe-down, tire dressing, and a spray wax for added protection. This service is perfect for regular upkeep and preserving your vehicle\'s appearance.'
    },
    {
      id: 5,
      title: 'Ceramic Coating',
      icon: 'fa-shield-alt',
      shortDesc: 'Long-lasting protection with advanced ceramic technology.',
      longDesc: 'Our ceramic coating service provides the ultimate protection for your vehicle\'s paint. This advanced nano-ceramic technology creates a permanent bond with your car\'s paint, offering superior protection against UV rays, chemical contaminants, and minor scratches. The hydrophobic properties make cleaning easier and maintain a deep, glossy finish that lasts for years.'
    },
    {
      id: 6,
      title: 'Paint Protection Film',
      icon: 'fa-layer-group',
      shortDesc: 'Premium clear film protection against rock chips and scratches.',
      longDesc: 'Our Paint Protection Film (PPF) service offers the highest level of protection for your vehicle\'s paint. This virtually invisible urethane film is applied to high-impact areas to prevent damage from rock chips, road debris, and minor scratches. The self-healing technology allows minor scratches to disappear with heat, keeping your car looking pristine for years to come.'
    }
  ];
  
  const toggleServiceDetails = (id) => {
    setActiveService(activeService === id ? null : id);
  };
  
  return (
    <section className="services section">
      <div className="container">
        <h2 className="section-title">Our Detailing Services</h2>
        <div className="services-grid grid">
          {services.map(service => (
            <div 
              className={`service-card card ${activeService === service.id ? 'flipped' : ''}`} 
              key={service.id}
              onClick={() => toggleServiceDetails(service.id)}
            >
              <div className="service-icon">
                <i className={`fas ${service.icon}`}></i>
              </div>
              <div className="card-content">
                <h3 className="card-title">{service.title}</h3>
                <p className="card-text">{service.shortDesc}</p>
                <div className="service-details">
                  <p>{service.longDesc}</p>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Services;
